  #{smsChannelCode,jdbcType=CHAR}, #{sendObject,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR}, 
  #{updateStaffId,jdbcType=CHAR}, #{updateDepartId,jdbcType=CHAR}, #{updateTime,jdbcType=DATE}
  )

mapper.update:
